section.artwork:
; CBOR tag wrappers pop-evidence = #6.1129336656(evidence-packet) pop-war = #6.1129791826(attestation-result)
; Primary structures evidence-packet = { 1 => uint, ; version (MUST be 1) 2 => tstr, ; profile-uri 3 => uuid, ; packet-id 4 => pop-timestamp, ; created 5 => document-ref, ; document 6 => [3* checkpoint], ; checkpoints (min 3) ? 7 => attestation-tier, ; T1-T4 ? 8 => [* tstr], ; limitations ? 9 => profile-declaration, ; profile ? 10 => [+ presence-challenge], ; QR/OOB proofs ? 11 => channel-binding, ; TLS EKM binding ; keys 14-17 reserved for future use ? 13 => content-tier, ; Evidence Content Tier ? 18 => physical-liveness, ; physical-liveness markers * int => any, ; extension fields }
checkpoint = { 1 => uint, ; sequence (monotonic) 2 => uuid, ; checkpoint-id 3 => pop-timestamp, ; timestamp (local) 4 => hash-value, ; content-hash 5 => uint, ; char-count 6 => edit-delta, ; delta 7 => hash-value, ; prev-hash 8 => hash-value, ; checkpoint-hash 9 => process-proof, ; SWF proof ? 10 => jitter-binding, ; behavioral-entropy (ENHANCED+) ? 11 => physical-state, ; CDCE Weave (ENHANCED+) ? 12 => bstr .size 32, ; entangled-mac (ENHANCED+) * int => any, ; extension fields }
document-ref = { 1 => hash-value, ; content-hash ? 2 => tstr, ; filename 3 => uint, ; byte-length 4 => uint, ; char-count ? 5 => hash-salt-mode, ; salting mode ? 6 => bstr .size 32, ; salt-commitment }
process-proof = { 1 => proof-algorithm, ; algorithm id 2 => proof-params, ; SWF params 3 => bstr .size 32, ; input (seed) 4 => bstr .size 32, ; merkle-root 5 => [+ merkle-proof], ; sampled proofs 6 => float32, ; claimed-duration (seconds) }
; Subsidiary type definitions attestation-tier = &( software-only: 1, ; T1: AAL1 attested-software: 2, ; T2: AAL2 hardware-bound: 3, ; T3: AAL3 hardware-hardened: 4, ; T4: LoA4 )
content-tier = &( core: 1, enhanced: 2, maximum: 3, )
proof-algorithm = &( ; 1 is reserved for future use pobst-argon2id: 20, )
hash-salt-mode = &( unsalted: 0, author-salted: 1, )
proof-params = { 1 => uint, ; time-cost (t) 2 => uint, ; memory-cost (m, KiB) 3 => uint, ; parallelism (p) 4 => uint, ; iterations }
jitter-binding = { 1 => [+ float32], ; intervals (ms) 2 => float32, ; entropy-estimate (bits) 3 => bstr .size 32, ; jitter-seal (HMAC) }
merkle-proof = { 1 => uint, ; leaf-index 2 => [+ bstr .size 32], ; sibling-path 3 => bstr .size 32, ; leaf-value }
edit-delta = { 1 => uint, ; chars-added 2 => uint, ; chars-deleted 3 => uint, ; op-count ? 4 => [* edit-position], ; positions }
edit-position = [ uint, ; offset int, ; change (+/-), MUST be non-zero ]
physical-state = { 1 => [+ float32], ; thermal (relative) 2 => int, ; entropy-delta (signed) ? 3 => bstr .size 32, ; kernel-commitment }
physical-liveness = { 1 => [+ thermal-sample], ; thermal trajectory 2 => bstr .size 32, ; entropy-anchor }
thermal-sample = [ pop-timestamp, ; sample time float32, ; temperature delta ]
presence-challenge = { 1 => bstr .size (16..256), ; challenge-nonce (128+ bits) 2 => bstr, ; device-signature (MUST be COSE_Sign1) 3 => pop-timestamp, ; response-time }
profile-declaration = { 1 => tstr, ; profile-id 2 => [+ uint], ; feature-flags }
binding-type = &( tls-exporter: 1, )
channel-binding = { 1 => binding-type, ; binding-type 2 => bstr .size 32, ; binding-value (EKM output) }
; Base types uuid = bstr .size 16 pop-timestamp = #6.1(float32) ; CBOR tag 1 (epoch-based, float32) hash-value = { 1 => hash-algorithm, 2 => bstr, } hash-algorithm = &( sha256: 1, sha384: 2, sha512: 3, )
